class ListNode {
 	 int val;
 	 ListNode next;
 	 ListNode(int x) {
 		 val = x;
 		 next = null;
 	 }
 }

public class TreeNode {
 	 int val;
 	 TreeNode left;
 	 TreeNode right;
 	 TreeNode(int x) { val = x; }
 }

/** * Definition for a binary tree node.
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
 public class Solution {
 	 public boolean isValidBST(TreeNode root) {
 		 return isValidBST(root, Double.NEGATIVE_INFINITY, Double.POSITIVE_INFINITY);
 	 }
 	 public boolean isValidBST(TreeNode root, double min, double max){

	 	 if(root == null) return true;
 	 	 double value = root.val * 1.0;
 	 	 if(root.val < = min || root.val >= max) return false;

 	 	 return isValidBST(root.left, min, value) && isValidBST(root.right, value, max);
 	 }
 }

public final class HuffmanCompress {

 	 public static void main(String[] args) throws IOException {
 	\ // Show what command line arguments to use
 	\ if (args.length == 0) {
 		 System.err.println("Usage: java HuffmanCompress InputFile OutputFile");
 		 System.exit(1);
 		 return;
 	\ }

 		 // Otherwise, compress
 		 File inputFile = new File(args[0]);
 		 File outputFile = new File(args[1]);

 		 // Read input file once to compute symbol frequencies
 		 // The resulting generated code is optimal for static Huffman coding and also canonical
 		 FrequencyTable freq = getFrequencies(inputFile);
 		 freq.increment(256); // EOF symbol gets a frequency of 1
 		 CodeTree code = freq.buildCodeTree();
 		 CanonicalCode canonCode = new CanonicalCode(code, 257);
 		 code = canonCode.toCodeTree(); // Replace code tree with canonical one. For each symbol, the code value may change but the code length stays the same.

 		 // Read input file again, compress with Huffman coding, and write output file
 		 InputStream in = new BufferedInputStream(new FileInputStream(inputFile));
 		 BitOutputStream out = new BitOutputStream(new BufferedOutputStream(new FileOutputStream(outputFile)));
 		 try {
 		 writeCode(out, canonCode);
 		 compress(code, in, out);
 	 } finally {
 		 out.close();
 		 in.close();
 	 }
 }

 	 private static FrequencyTable getFrequencies(File file) throws IOException {
 		 FrequencyTable freq = new FrequencyTable(new int[257]);
 		 InputStream input = new BufferedInputStream(new FileInputStream(file));
 		 try {
 			 while (true) {
 			 int b = input.read();
 			 if (b == -1)
 				 break;
 			 freq.increment(b);
 		 }
 	 } finally {
 		 input.close();
 	 }
 		 return freq;
 }

 	 static void writeCode(BitOutputStream out, CanonicalCode canonCode) throws IOException {
 		 for (int i = 0; i < canonCode.getSymbolLimit(); i++) {
 			 int val = canonCode.getCodeLength(i);
 			 // For this file format, we only support codes up to 255 bits long
 			 if (val >= 256)
 				 throw new RuntimeException("The code for a symbol is too long");

 			 // Write value as 8 bits in big endian
 			 for (int j = 7; j >= 0; j--)
 				 out.write((val >>> j) & 1);
 		 }
 	 }
 	 static void compress(CodeTree code, InputStream in, BitOutputStream out) throws IOException {
 		 HuffmanEncoder enc = new HuffmanEncoder(out);
 		 enc.codeTree = code;
 		 while (true) {
 		 int b = in.read();
 		 if (b == -1)
 		 break;
 		 enc.write(b);
 	 }
 	 enc.write(256); // EOF
 }
 }

public class PriorityQueueTest {

 	 static class PQsort implements Comparator <Integer> {

 		 public int compare(Integer one, Integer two) {
 			 return two - one;
 		 }
 	 }

 	 public static void main(String[] args) {
 		 int[] ia = { 1, 10, 5, 3, 4, 7, 6, 9, 8 };
 		 PriorityQueue <Integer> pq1 = new PriorityQueue <Integer>();

 		 // use offer() method to add elements to the PriorityQueue pq1
 		 for (int x : ia) {
 			 pq1.offer(x);
 		 }

 		 System.out.println("pq1: " + pq1);

 		 PQsort pqs = new PQsort();
 		 PriorityQueue <Integer> pq2 = new PriorityQueue <Integer>(10, pqs);
 		 // In this particular case, we can simply use Collections.reverseOrder()
 		 // instead of self-defined comparator
 		 for (int x : ia) {
 			 pq2.offer(x);
 		 }

 		 System.out.println("pq2: " + pq2);

 		 // print size
 		 System.out.println("size: " + pq2.size());
 		 // return highest priority element in the queue without removing it
 		 System.out.println("peek: " + pq2.peek());
 		 // print size
 		 System.out.println("size: " + pq2.size());
 		 // return highest priority element and removes it from the queue
 		 System.out.println("poll: " + pq2.poll());
 		 // print size
 		 System.out.println("size: " + pq2.size());

 		 System.out.print("pq2: " + pq2);

 	 }
 }

class BubbleSort {
 	 public static void main(String []args) {
 		 int n, c, d, swap;
 		 Scanner in = new Scanner(System.in);

 		 System.out.println("Input number of integers to sort");
 		 n = in.nextInt();

 		 int array[] = new int[n];

 		 System.out.println("Enter " + n + " integers");

 		 for (c = 0; c < n; c++)
 			 array[c] = in.nextInt();

 		 for (c = 0; c < ( n - 1 ); c++) {
 			 for (d = 0; d < n - c - 1; d++) {
 			 if (array[d] > array[d+1]) /* For descending order use < */
 {
 				 swap = array[d];
 				 array[d] = array[d+1];
 				 array[d+1] = swap;
 			 }
 		 }
 	 }

 	 System.out.println("Sorted list of numbers");

 	 for (c = 0; c < n; c++)
 	 System.out.println(array[c]);
 }
 }

public class MySelectionSort {
 	 public static int[] doSelectionSort(int[] arr){

 		 for (int i = 0; i < arr.length - 1; i++)
 		 {
 			 int index = i;
 			 for (int j = i + 1; j < arr.length; j++)
 				 if (arr[j] < arr[index])
 				 index = j;

 			 int smallerNumber = arr[index];
 			 arr[index] = arr[i];
 			 arr[i] = smallerNumber;
 		 }
 		 return arr;
 	 }

 	 public static void main(String a[]){

 		 int[] arr1 = {10,34,2,56,7,67,88,42};
 		 int[] arr2 = doSelectionSort(arr1);
 		 for(int i:arr2){
 			 System.out.print(i);
 			 System.out.print(", ");
 		 }
 	 }
 }

public class MyInsertionSort {

public static void main(String[] args) {

	 int[] input = { 4, 2, 9, 6, 23, 12, 34, 0, 1 };
 	 insertionSort(input);
 }

 private static void printNumbers(int[] input) {

 	 for (int i = 0; i < input.length; i++) {
 		 System.out.print(input[i] + ", ");
 	 }
 	 System.out.println("
");
 }
 public static void insertionSort(int array[]) {
 	 int n = array.length;
 	 for (int j = 1; j < n; j++) {
 		 int key = array[j];
 		 int i = j-1;
 		 while ( (i > -1) && ( array [i] > key ) ) {
 			 array [i+1] = array [i];
 			 i--;
 		 }
 		 array[i+1] = key;
 		 printNumbers(array);
 	 }
 }
 }

TopDownMergeSort(A[], B[], n){
 	 TopDownSplitMerge(A, 0, n, B);
 }

// iBegin is inclusive; iEnd is exclusive (A[iEnd] is not in the set). TopDownSplitMerge(A[], iBegin, iEnd, B[]){
 	 if(iEnd - iBegin < 2) // if run size == 1
 		 return; // consider it sorted
 	 // recursively split runs into two halves until run size == 1,
 	 // then merge them and return back up the call chain
 	 iMiddle = (iEnd + iBegin) / 2; // iMiddle = mid point
 	 TopDownSplitMerge(A, iBegin, iMiddle, B); // split / merge left half
 	 TopDownSplitMerge(A, iMiddle, iEnd, B); // split / merge right half
 	 TopDownMerge(A, iBegin, iMiddle, iEnd, B); // merge the two half runs
 	 CopyArray(B, iBegin, iEnd, A); // copy the merged runs back to A
 }

 // Left half is A[iBegin :iMiddle-1].
 // Right half is A[iMiddle:iEnd-1 ].
 TopDownMerge(A[], iBegin, iMiddle, iEnd, B[])
 {
 	 i = iBegin, j = iMiddle;

 	 // While there are elements in the left or right runs...
 	 for (k = iBegin; k < iEnd; k++) {
 		 // If left run head exists and is <= existing right run head.
 		 if (i < iMiddle &&(j >= iEnd || A[i] <= A[j])) {
 			 B[k] = A[i];
 			 i = i + 1;
 		 } else {
 			 B[k] = A[j];
 			 j = j + 1;
 		 }
 	 }
 }

 CopyArray(B[], iBegin, iEnd, A[]){
 	 for(k = iBegin; k < iEnd; k++)
 		 A[k] = B[k];
 }
 	 //Bottom-up implementation
 	 //**********************************************//
 	 void BottomUpMergeSort(A[], B[], n)
 {
 	 // Each 1-element run in A is already "sorted".
 	 // Make successively longer sorted runs of length 2, 4, 8, 16... until whole array is sorted.
 	 for (width = 1; width < n; width = 2 * width)
 	 {
 		 // Array A is full of runs of length width.
 		 for (i = 0; i < n; i = i + 2 * width)
 		 {
 			 // Merge two runs: A[i:i+width-1] and A[i+width:i+2*width-1] to B[]
 			 // or copy A[i:n-1] to B[] ( if(i+width >= n) )
 			 BottomUpMerge(A, i, min(i+width, n), min(i+2*width, n), B);
 		 }
 		 // Now work array B is full of runs of length 2*width.
 		 // Copy array B to array A for next iteration.
 		 // A more efficient implementation would swap the roles of A and B.
 		 CopyArray(B, A, n);
 		 // Now array A is full of runs of length 2*width.
 	 }
 }

	// Left run is A[iLeft :iRight-1].
 	// Right run is A[iRight:iEnd-1 ].
 	BottomUpMerge(A[], iLeft, iRight, iEnd, B[])
 {
 	 i = iLeft, j = iRight;
 	 // While there are elements in the left or right runs...
 	 for (k = iLeft; k < iEnd; k++) {
 		 // If left run head exists and is <= existing right run head.
 		 if (i < iRight && (j >= iEnd || A[i] <= A[j])) {
 			 B[k] = A[i];
 			 i = i + 1;
 		 } else {
 			 B[k] = A[j];
 			 j = j + 1;
 		 }
 	 }
 }

void CopyArray(B[], A[], n){
 	 for(i = 0; i < n; i++)
 		 A[i] = B[i];
 }

public class MyQuickSort {

 	 private int array[];
 	 private int length;
 	 	 public void sort(int[] inputArr) {
 	 		 if (inputArr == null || inputArr.length == 0) {
 		 return;
 	 }
 		 this.array = inputArr;
 		 length = inputArr.length;
 		 quickSort(0, length - 1);
 	 }

 	 private void quickSort(int lowerIndex, int higherIndex) {

 		 int i = lowerIndex;
 		 int j = higherIndex;
 		 // calculate pivot number, I am taking pivot as middle index number
 		 int pivot = array[lowerIndex+(higherIndex-lowerIndex)/2];
 		 // Divide into two arrays
 		 while (i <= j) {
 		 /**
 			 * In each iteration, we will identify a number from left side which
 			 * is greater then the pivot value, and also we will identify a number
 			 * from right side which is less then the pivot value. Once the search
 			 * is done, then we exchange both numbers.
 			 */
 			 while (array[i] < pivot) {
 				 i++;
 			 }
 			 while (array[j] > pivot) {
 				 j--;
 			 }
 			 if (i <= j) {
 				 exchangeNumbers(i, j);
 				 //move index to next position on both sides
 				 i++;
 				 j--;
 			 }
 		 }
 // call quickSort() method recursively
 		 if (lowerIndex < j)
 			 quickSort(lowerIndex, j);
 		 if (i < higherIndex)
 			 quickSort(i, higherIndex);
 		 }

 		 private void exchangeNumbers(int i, int j) {
 			 int temp = array[i];
 			 array[i] = array[j];
 			 array[j] = temp;
 		 }

 		 public static void main(String a[]){

 		 MyQuickSort sorter = new MyQuickSort();
 		 int[] input = {24,2,45,20,56,75,2,56,99,53,12};
 		 sorter.sort(input);
 		 for(int i:input){
 			 System.out.print(i);
 			 System.out.print(" ");
 		 }
 	 }
 }

public class HeapSort
 {
 	 private static int N;
 	 /* Sort Function */
 	 public static void sort(int arr[])
 	 {
 		 heapify(arr);
 		 for (int i = N; i > 0; i--)
 		 {
 			 swap(arr,0, i);
 			 N = N-1;
 			 maxheap(arr, 0);
 		 }
 	 }
 	 /* Function to build a heap */
 	 public static void heapify(int arr[])
 	 {
 		 N = arr.length-1;
 		 for (int i = N/2; i >= 0; i--)
 			 maxheap(arr, i);
 	 }
 	 /* Function to swap largest element in heap */
 	 public static void maxheap(int arr[], int i)
 	 {
 		 int left = 2*i ;
 		 int right = 2*i + 1;
 		 int max = i;
 		 if (left <= N && arr[left] > arr[i])
 			 max = left;
 		 if (right <= N && arr[right] > arr[max])
 			 max = right;

 		 if (max != i)
 		 {
 			 swap(arr, i, max);
 			 maxheap(arr, max);
 		 }
 	 }
 	 /* Function to swap two numbers in an array */
 	 public static void swap(int arr[], int i, int j)
 	 {
 		 int tmp = arr[i];
 		 arr[i] = arr[j];
 		 arr[j] = tmp;
 	 }
 	 /* Main method */
 	 public static void main(String[] args)
 	 {
 		\ Scanner scan = new Scanner( System.in );
 		 System.out.println("Heap Sort Test
");
 		 int n, i;
 		 /* Accept number of elements */
 		 System.out.println("Enter number of integer elements");
 		 n = scan.nextInt();
 		 /* Make array of n elements */
 		 int arr[] = new int[ n ];
 		 /* Accept elements */
 		 System.out.println("
Enter "+ n +" integer elements");
 		 for (i = 0; i < n; i++)
 			 arr[i] = scan.nextInt();
 		 /* Call method sort */
 		 sort(arr);
 		 /* Print sorted Array */
 		 System.out.println("
Elements after sorting ");
 		 for (i = 0; i < n; i++)
 			 System.out.print(arr[i]+" ");
 		 System.out.println();
 	 }
 }

public class BucketSort{

 	 public static void sort(int[] a, int maxVal) {
 		 int [] bucket=new int[maxVal+1];

 		 for (int i=0; i<bucket.length; i++) {
 			 bucket[i]=0;
 		 }

 		 for (int i=0; i<a.length; i++) {
 			 bucket[a[i]]++;
 		 }

 		 int outPos=0;
 		 for (int i=0; i<bucket.length; i++) {
 			 for (int j=0; j<bucket[i]; j++) {
 				 a[outPos++]=i;
 			 }
 		 }
 	 }

 	 public static void main(String[] args) {
 		 int maxVal=5;
 		 int [] data= {5,3,0,2,4,1,0,5,2,3,1,4};

 		 System.out.println("Before: " + Arrays.toString(data));
 		 sort(data,maxVal);
 		 System.out.println("After: " + Arrays.toString(data));
 	 }
 }

int[] countingSort(int[] a, int k) {
 	 int c[] = new int[k];
 	 for (int i = 0; i < a.length; i++)
 		 c[a[i]]++;
 	 for (int i = 1; i < k; i++)
 		 c[i] += c[i-1];
 	 int b[] = new int[a.length];
 	 for (int i = a.length-1; i >= 0; i--)
 		 b[--c[a[i]]] = a[i];
 	 return b;
 }

public List<Integer> majorityElement(int[] nums) {
 	 List<Integer> rt = new ArrayList<Integer>();
 	 if (nums == null || nums.length == 0) {
 		 return rt;
 	 }
 	 int m1 = nums[0];
 	 int m2 = 0;
 	 int c1 = 1;
 	 int c2 = 0;
 	 for (int i = 1; i < nums.length; i++) {
 		 int x = nums[i];
 		 if (x == m1) {
 			 c1++;
 		 } else if (x == m2) {
 			 c2++;
 		 } else if (c1 == 0) {
 			 m1 = x;
 			 c1 = 1;
 		 } else if (c2 == 0) {
 			 m2 = x;
 			 c2 = 1;
 		 } else {
 			 c1--;
 			 c2--;
 		 }
 	 }
 	 c1 = 0;
 	 c2 = 0;
 	 for (int i = 0; i < nums.length; i++) {
 		 if (m1 == nums[i]) {
 			 c1++;
 		 } else if (m2 == nums[i]) {
 			 c2++;
 		 }
 	 }
 	 if (c1 > nums.length / 3) {
 		 rt.add(m1);
 	 }
 	 if (c2 > nums.length / 3) {
 		 rt.add(m2);
 	 }
 	 return rt;
 }

/**
 * http://www.jiuzhang.com//solutions/implement-strstr
 */
 class Solution {
 	 /**
 	 * Returns a index to the first occurrence of target in source,
 	 * or -1 if target is not part of source.
 	 * @param source string to be scanned.
 	 * @param target string containing the sequence of characters to match.
 	 */
 	 public int strStr(String source, String target) {
 		 if (source == null || target == null) {
 		 return -1;
 	 }

	 int i, j;
 	 for (i = 0; i < source.length() - target.length() + 1; i++) {
 		 for (j = 0; j < target.length(); j++) {
 			 if (source.charAt(i + j) != target.charAt(j)) {
 				 break;
 			 } //if
 		 } //for j
 		 if (j == target.length()) {
 			 return i;
 		 }
 		 } //for i

 		 // did not find the target
 		 return -1;
 	 }
 }

final static int LETTERS_LEN = 256; 

public static boolean isAnagram(String s1, String s2) {
 	 if (s1 == null || s2 == null)
 		 return false;
 	 int len = s1.length();
 	 if (len != s2.length() || len < 2)
 		 return false;

	 int[] letters = new int[LETTERS_LEN];

	 for (int i = 0; i < len; i++) {
 		 letters[s1.charAt(i)]++;
 		 letters[s2.charAt(i)]--;
 	 }

	 for (int i = 0; i < LETTERS_LEN; i++) {
 		 if (letters[i] != 0) {
 			 return false;
 		 }
 	 }
 	 return true;
 }

public int compareVersion(String version1, String version2) {
	 	 String[] arr1 = version1.split("\.");
 	 String[] arr2 = version2.split("\.");

	 int i=0;
 	 while(i<arr1.length || i<arr2.length){
 		 if(i<arr1.length && i<arr2.length){
 			 if(Integer.parseInt(arr1[i]) < Integer.parseInt(arr2[i])){
 				 return -1;
 			 }else if(Integer.parseInt(arr1[i]) > Integer.parseInt(arr2[i])){
 				 return 1;
 			 }
 		 } else if(i<arr1.length){
 			 if(Integer.parseInt(arr1[i]) != 0){
 				 return 1;
 			 }
 		 } else if(i<arr2.length){
 			 if(Integer.parseInt(arr2[i]) != 0){
 				 return -1;
 			 }
 		 }
 		 i++;
 	 }
 	 return 0;
 }

public class Solution {
 	 public List<String> anagrams(String[] strs) {
 		 HashMap<String, LinkedList<String>> dict = new HashMap<>();
 		 for (int i = 0; i < strs.length; i++) {
 			 String s = strs[i];
 			 char[] chars = s.toCharArray();
 			 Arrays.sort(chars);
 			 String sortedString = String.valueOf(chars);
 			 if (dict.containsKey(sortedString)) {
 				 dict.get(sortedString).add(s);
 			 } else {
 				 LinkedList<String> list = new LinkedList<>();
 				 list.add(s);
 				 dict.put(sortedString, list);
 			 }
 		 }
 		 List<String> ret = new LinkedList<>();
 		 for (LinkedList<String> list : dict.values()) {
 			 if (list.size() > 1)
 			 ret.addAll(list);
 		 }
 		 return ret;
 	 }
 }

public class Solution {
 	 /**
 	 * @param A, B: Two string.
 	 * @return: the length of the longest common substring.
 	 */
 	 public int longestCommonSubstring(String A, String B) {
 		 // write your code here
 		 int maxlen = 0;
 		 int xlen = A.length();
 		 int ylen = B.length();
 		 for(int i = 0; i < xlen; ++i)
 		 {
 			 for(int j = 0; j < ylen; ++j)
 			 {
 				 int len = 0;
 				 while (i + len < xlen && j + len < ylen && 				 A.charAt(i + len) == B.charAt(j + len))
 					 len ++;
 				 if(len > maxlen)
 					 maxlen = len;
 			 }
 		 }
 		 return maxlen;
 	 }
 }

public class Solution {
 	 public int removeElement(int[] A, int elem) {
 		 int count = 0;
 		 for(int i=0; i<A.length; i++){
 			 if(A[i] != elem){
 				 A[count] = A[i];
 				 count++;
 			 }
 		 }
 		 return count;
 	 }
 }

public class Solution {
 /**
 * @param nums: A list of integers
 * @return: A integer indicate the sum of max subarray
 */
 	 public int maxSubArray(ArrayList<Integer> nums) {
 		 // -1 is not proper for illegal input
 		 if (nums == null || nums.isEmpty()) return -1;

 		 int sum = 0, maxSub = Integer.MIN_VALUE;
 		 for (int num : nums) {
 			 // drop negtive sum
 			 sum = Math.max(sum, 0);
 			 sum += num;
 			 // update maxSub
 			 maxSub = Math.max(maxSub, sum);
 		 }
 		 return maxSub;
 	 }
 }

public class Solution {
 	 public int maxSubArrayLen(int[] nums, int k) {
 		 if (nums==null || nums.length==0) return 0;
 		 HashMap<Integer, Integer> map = new HashMap<Integer, Integer>();
 		 map.put(0, -1);
 		 int sum = 0;
 		 int maxLen = Integer.MIN_VALUE;
 		 for (int i=0; i<nums.length; i++) {
 			 sum += nums[i];
 			 if (!map.containsKey(sum)) {
 				 map.put(sum, i);
 			 }
 			 if (map.containsKey(sum-k)) {
 				 int index = map.get(sum-k);
 				 maxLen = Math.max(maxLen, i-index);
 			 }
 		 }
 		 return maxLen==Integer.MIN_VALUE? 0 : maxLen;
 	 }
 }

class Pair {
 	 int sum;
 	 int index;
 	 public Pair(int s, int i) {
 		 sum = s;
 		 index = i;
 	 }
 }

public class Solution {
 /**
 * @param nums: A list of integers
 * @return: A list of integers includes the index of the first number
 * and the index of the last number
 */
 	 public int[] subarraySumClosest(int[] nums) {
 		 int[] res = new int[2];
 		 if (nums == null || nums.length == 0) {
 			 return res;
 		 }

 		 int len = nums.length;
 		 if(len == 1) {
 			 res[0] = res[1] = 0;
 			 return res;
 		 }
 		 Pair[] sums = new Pair[len+1];
 		 int prev = 0;
 		 sums[0] = new Pair(0, 0);
 		 for (int i = 1; i <= len; i++) {
 			 sums[i] = new Pair(prev + nums[i-1], i);
 			 prev = sums[i].sum;
 		 }
 		 Arrays.sort(sums, new Comparator<Pair>() {
 			 public int compare(Pair a, Pair b) {
 				 return a.sum - b.sum;
 			 }
 		 });
 		 int ans = Integer.MAX_VALUE;
 		 for (int i = 1; i <= len; i++) {

 			 if (ans > sums[i].sum - sums[i-1].sum) {
 				 ans = sums[i].sum - sums[i-1].sum;
 				 int[] temp = new int[]{sums[i].index - 1, sums[i - 1].index - 1};
 				 Arrays.sort(temp);
 				 res[0] = temp[0] + 1;
 				 res[1] = temp[1];
 			 }
 		 }
 		 return res;
 	 }
 }

public int[] productExceptSelf(int[] nums) {
 	 int[] result = new int[nums.length];
 	 int[] t1 = new int[nums.length];
 	 int[] t2 = new int[nums.length];
 	 t1[0]=1;
 	 t2[nums.length-1]=1;

 	 //scan from left to right
 	 for(int i=0; i<nums.length-1; i++){
 		 t1[i+1] = nums[i] * t1[i];
 	 }

 	 //scan from right to left
 	 for(int i=nums.length-1; i>0; i--){
 		 t2[i-1] = t2[i] * nums[i];
 	 }

 	 //multiply
 	 for(int i=0; i<nums.length; i++){
 		 result[i] = t1[i] * t2[i];
 	 }
 	 return result;
 }

public class Solution {
 /**
 *@param nums: The integer array you should partition
 *@param k: As description
 *return: The index after partition
 */
 	 public int partitionArray(int[] nums, int k) {
 		 //write your code here
 		 if(nums == null || nums.length == 0){
 			 return 0;
 		 }
 		 // //dummy number
 		 // nums.add(k);

 		 int i = 0;
 		 int j = nums.length - 1;
 		 for(; i <= j; i++){
 			 if(nums[i]< k){
 				 continue;
 			 }

 			 while(j >= i && nums[j] >= k){
 				 j --;
 			 }//while

 			 //i points to a number >= k
 			 //j points to a number < k
 			 //swap i and j
 			 if(j >= 0 && i < j){
 				 int tmp = nums[i];
 				 nums[i] = nums[j];
 				 nums[j] = tmp;
 				 j --;
 			 }

 		 }//for

 		 // nums.remove(j + 1);
 		 return j + 1;
 	 }//partitionArray
 }

int firstMissingPositiveAnd0(int A[]) {
 	 int n = A.length;
 	 for (int i = 0; i < n; i++) {
 		 // when the ith element is not i
 		 while (A[i] != i) {
 			 // no need to swap when ith element is out of range [0,n]
 			 if (A[i] < 0 || A[i] >= n)
 				 break;

 			 //handle duplicate elements
 			 if(A[i]==A[A[i]])
 				 break;
 			 // swap elements
 			 int temp = A[i];
 			 A[i] = A[temp];
 			 A[temp] = temp;
 		 }
 	 }
 	 for (int i = 0; i < n; i++) {
 		 if (A[i] != i)
 			 return i;
 	 }
 	 return n;
 }

public static int[] twoSum(int[] numbers, int target) {
 	 int[] ret = new int[2];
 	 for (int i = 0; i < numbers.length; i++) {
 		 for (int j = i + 1; j < numbers.length; j++) {
 			 if (numbers[i] + numbers[j] == target) {
 				 ret[0] = i + 1;
 				 ret[1] = j + 1;
 			 }
 		 }
 	 }
 	 return ret;
 }

public class Solution {
 	 public ArrayList<ArrayList<Integer>> threeSum(int[] num) {
 		 //sort array
 		 Arrays.sort(num);
 		 ArrayList<ArrayList<Integer>> result = new ArrayList<ArrayList<Integer>>();
 		 ArrayList<Integer> each = new ArrayList<Integer>();
 		 for(int i=0; i<num.length; i++){
 			 if(num[i] > 0) break;

 			 for(int j=i+1; j<num.length; j++){
 				 if(num[i] + num[j] > 0 && num[j] > 0) break;

 				 for(int k=j+1; k<num.length; k++){
 					 if(num[i] + num[j] + num[k] == 0) {
 						 each.add(num[i]);
 						 each.add(num[j]);
 						 each.add(num[k]);
 						 result.add(each);
 						 each.clear();
 					 }
 				 }
 			 }
 		 }
 		 return result;
 	 }
 }

public int threeSumClosest(int[] nums, int target) {
 	 int min = Integer.MAX_VALUE;
 	 int result = 0;

 	 Arrays.sort(nums);

 	 for (int i = 0; i < nums.length; i++) {
 		 int j = i + 1;
 		 int k = nums.length - 1;
 		 while (j < k) {
 			 int sum = nums[i] + nums[j] + nums[k];
 			 int diff = Math.abs(sum - target);

 			 if(diff == 0) return sum;

 			 if (diff < min) {
 				 min = diff;
 				 result = sum;
 			 }
 			 if (sum <= target) {
 				 j++;
 			 } else {
 				 k--;
 			 }
 		 }
 	 }

 	 return result;
 }

/**
 *
 * Remove Duplicates from Sorted Array
 *
 * Given a sorted array, remove the duplicates in place such that each element appear
 * only once and return the new length.
 *
 * Do not allocate extra space for another array, you must do this in place with
 * constant memory.
 *
 * For example, Given input array A = [1,1,2],
 *
 * Your function should return length = 2, and A is now [1,2].
 *
 */
 public class S26 {

 	 public static void main(String[] args) {
 		 int[] A = {1, 1, 2};
 		 System.out.println(removeDuplicates(A));
 	 }

 	 public static int removeDuplicates(int[] A) {
 		 int len = A.length;
 		 if(len < 2){
 			 return len;
 		 }

 		 int i = 0, j = 1;
 		 while(i<len && j<len){
 			 if(A[i] == A[j]){
 				 j++;
 			 }else{
 				 i++;
 				 A[i] = A[j];
 				 j++;
 			 }
 		 }
 //
 		 return i+1;
 	 }
 }

public class Solution {
 	 public int removeDuplicates(int[] A) {
 		 if(A == null || A.length == 0)
 			 return 0;
 		 int pre = A[0];
 		 boolean flag = false;
 		 int count = 0;
 		 for(int i=1; i<A.length; i++){
 			 int curr = A[i];
 			 if(curr == pre){
 				 if(!flag){
 					 flag = true;
 					 continue;
 				 }else{
 					 count++;
 				 }
 			 }else{
 				 pre = curr;
 				 flag = false;
 			 }
 		 }
 		 return A.length - count;
 	 }
 }

class Solution { /**
 * @param A: sorted integer array A which has m elements,
 * but size of A is m+n
 * @param B: sorted integer array B which has n elements
 * @return: void
 */
 	 public void mergeSortedArray(int[] A, int m, int[] B, int n) {
 		 int i = m-1, j = n-1, index = m + n - 1;
 		 while (i >= 0 && j >= 0) {
 			 if (A[i] > B[j]) {
 				 A[index--] = A[i--];
 			 } else {
 				 A[index--] = B[j--];
 			 }
 		 }
 		 while (i >= 0) {
 			 A[index--] = A[i--];
 		 }
 		 while (j >= 0) {
 			 A[index--] = B[j--];
 		 }
 	 }
 }

class Solution {
 /**
 * @param A: sorted integer array A which has m elements,
 * but size of A is m+n
 * @param B: sorted integer array B which has n elements
 * @return: void
 */
 	 public void mergeSortedArray(int A[], int m, int B[], int n) {
 		 int index = n + m;

 		 while (m > 0 && n > 0) {
 			 if (A[m - 1] > B[n - 1]) {
 				 A[--index] = A[--m];
 			 } else {
 				 A[--index] = B[--n];
 			 }
 		 }
 		 while (n > 0) {
 			 A[--index] = B[--n];
 		 }
 		 while (m > 0) {
 			 A[--index] = A[--m];
 		 }
 	 }
 };

class Solution {
 /**
 * @param nums: The integer array.
 * @param target: Target to find.
 * @return: The first position of target. Position starts from 0.
 */
 	 public int binarySearch(int[] nums, int target) {
 		 if (nums == null || nums.length == 0) {
 			 return -1;
 		 }

 		 int start = 0;
 		 int end = nums.length - 1;
 		 int mid;
 		 while (start + 1 < end) {
 			 mid = start + (end - start) / 2; // avoid overflow when (end + start)
 			 if (target < nums[mid]) {
 				 end = mid;
 			 } else if (target > nums[mid]) {
 				 start = mid;
 			 } else {
 				 end = mid;
 			 }
 		 }

 		 if (nums[start] == target) {
 			 return start;
 		 }
 		 if (nums[end] == target) {
 			 return end;
 		 }

 		 return -1;
 	 }
 }

public class Solution {
 /**
 * param A : an integer sorted array
 * param target : an integer to be inserted
 * return : an integer
 */
 	 public int searchInsert(int[] A, int target) {
 	 if (A == null) {
 		 return -1;
 		 }
 		 if (A.length == 0) {
 			 return 0;
 		 }

 		 int start = 0, end = A.length - 1;
 		 int mid;

 		 while (start + 1 < end) {
 			 mid = start + (end - start) / 2;
 			 if (A[mid] == target) {
 				 return mid; // no duplicates, if not `end = target;`
 			 } else if (A[mid] < target) {
 				 start = mid;
 			 } else {
 				 end = mid;
 			 }
 		 }

 		 if (A[start] >= target) {
 			 return start;
 		 } else if (A[end] >= target) {
 			 return end; // in most cases
 		 } else {
 			 return end + 1; // A[end] < target;
 		 }
 	 }
 }

public class Solution {
 /**
 *@param A : an integer sorted array
 *@param target : an integer to be inserted
 *return : a list of length 2, [index1, index2]
 */
 	 public ArrayList searchRange(ArrayList A, int target) {
 		 ArrayList result = new ArrayList();
 		 int start, end, mid;
 		 result.add(-1);
 		 result.add(-1);

 		 if (A == null || A.size() == 0) {
 			 return result;
 		 }

 		 // search for left bound
 		 start = 0;
 		 end = A.size() - 1;
 		 while (start + 1 < end) {
 			 mid = start + (end - start) / 2;
 		 if (A.get(mid) == target) {
 			 end = mid; // set end = mid to find the minimum mid
 		 } else if (A.get(mid) > target) {
 			 end = mid;
 		 } else {
 			 start = mid;
 		 }
 	 }
 	 if (A.get(start) == target) {
 		 result.set(0, start);
 	 } else if (A.get(end) == target) {
 		 result.set(0, end);
 	 } else {
 		 return result;
 	 }

 	 // search for right bound
 	 start = 0;
 	 end = A.size() - 1;
 	 while (start + 1 < end) {
 		 mid = start + (end - start) / 2;
 		 if (A.get(mid) == target) {
 			 start = mid; // set start = mid to find the maximum mid
 		 } else if (A.get(mid) > target) {
 			 end = mid;
 		 } else {
 			 start = mid;
 		 }
 	 }
 	 if (A.get(end) == target) {
 		 result.set(1, end);
 	 } else if (A.get(start) == target) {
 		 result.set(1, start);
 	 } else {
 		 return result;
 	 }

 	 return result;
 		 // write your code here
 	 }
 }

/**
 * public class VersionControl {
 * public static boolean isBadVersion(int k);
 * }
 * you can use VersionControl.isBadVersion(k) to judge wether
 * the kth code version is bad or not.
 */
 class Solution {
 /**
 * @param n: An integers.
 * @return: An integer which is the first bad version.
 */
 	 public int findFirstBadVersion(int n) {
 		 // write your code here
 		 if (n == 0) {
 			 return -1;
 		 }

 		 int start = 1, end = n, mid;
 		 while (start + 1 < end) {
 			 mid = start + (end - start)/2;
 			 if (VersionControl.isBadVersion(mid) == false) {
 				 start = mid;
 			 } else {
 				 end = mid;
 			 }
 		 }

 		 if (VersionControl.isBadVersion(start) == true) {
 			 return start;
 		 } else if (VersionControl.isBadVersion(end) == true) {
 			 return end;
 		 } else {
 			 return -1; // not found
 		 }
 	 }
 }

public class Solution {
 	 public boolean searchMatrix(int[][] matrix, int target) {
 		 if(matrix==null || matrix.length==0 || matrix[0].length==0)
 			 return false;

 		 int m = matrix.length;
 		 int n = matrix[0].length;

 		 int start = 0;
 		 int end = m*n-1;

 		 while(start<=end){
 			 int mid=(start+end)/2;
 			 int midX=mid/n;
 			 int midY=mid%n;

 			 if(matrix[midX][midY]==target)
 				 return true;

			 if(matrix[midX][midY]<target){
 				 start=mid+1;
 			 }else{
 				 end=mid-1;
 			 }
 		 }

 		 return false;
 	 }
 }

class Solution {
 /**
 * @param A: An integers array.
 * @return: return any of peek positions.
 */
 	 public int findPeak(int[] A) {
 		 // write your code here
 		 if (A == null) {
 			 return -1;
 		 }
 		 if (A.length == 0) {
 			 return 0;
 		 }

 		 int start = 0, end = A.length - 1, mid = end / 2;
 		 while (start + 1 < end) {
 			 mid = start + (end - start)/2;
 			 if (A[mid] < A[mid - 1]) {
 				 end = mid;
 			 } else if (A[mid] < A[mid + 1]) {
 				 start = mid;
 			 } else {
 				 return mid;
 			 }
 		 }

 		 mid = (A[start] > A[end]) ? start : end;
 		 return mid;
 	 }
 }

public class Solution {
 /**
 *@param A : an integer rotated sorted array
 *@param target : an integer to be searched
 *return : an integer
 */
 	 public int search(int[] A, int target) {
 		 // write your code here
 		 if (A == null || A.length == 0) {
 			 return -1;
 		 }

 		 int start = 0, end = A.length - 1, mid = 0;
 		 while (start + 1 < end) {
 			 mid = start + (end - start)/2;
 			 if (A[mid] == target) {
 				 return mid;
 			 }
 			 if (A[start] < A[mid]) {//part 1
 				 if (A[start] <= target && target <= A[mid]) {
 					 end = mid;
 				 } else {
 					 start = mid;
 				 }
 			 } else { //part 2
 				 if (A[mid] <= target && target <= A[end]) {
 					 start = mid;
 				 } else {
 					 end = mid;
 				 }
 			 }
 		 } // end while

 		 if (A[start] == target) {
 			 return start;
 		 } else if (A[end] == target) {
 			 return end;
 		 } else {
 			 return -1; // not found
 		 }
 	 }
 }

public class Solution {
 /**
 * @param num: a rotated sorted array
 * @return: the minimum number in the array
 */
 	 public int findMin(int[] nums) {
 		 if (nums == null || nums.length == 0) {
 			 return -1;
 		 }

 		 int start = 0, end = nums.length - 1;
 		 int target = nums[nums.length - 1];

 		 // find the first element <= target
 		 while (start + 1 < end) {
 			 int mid = start + (end - start) / 2;
 			 if (nums[mid] <= target) {
 				 end = mid;
 			 } else {
 				 start = mid;
 			 }
 		 }
 		 if (nums[start] <= target) {
 			 return nums[start];
 		 } else {
 			 return nums[end];
 		 }
 	 }
 }

public class Solution {
 /**
 * @param matrix: A list of lists of integers
 * @param: A number you want to search in the matrix
 * @return: An integer indicate the occurrence of target in the given matrix
 */
 	 public int searchMatrix(int[][] matrix, int target) {
 		 int occurence = 0;

 		 if (matrix == null || matrix.length == 0) {
 			 return occurence;
 		 }
 		 if (matrix[0] == null || matrix[0].length == 0) {
 			 return occurence;
 		 }

 		 int row = matrix.length - 1;
 		 int column = matrix[0].length - 1;
 		 int index_row = 0, index_column = column;
 		 int number;

 		 if (target < matrix[0][0] || target > matrix[row][column]) {
 			 return occurence;
 		 }

 		 while (index_row < row + 1 && index_column + 1 > 0) {
 			 number = matrix[index_row][index_column];
 			 if (target == number) {
 				 occurence++;
 				 index_column--;
 			 } else if (target < number) {
 				 index_column--;
 			 } else if (target > number) {
 				 index_row++;
 			 }
 		 }
 		 return occurence;
 	 }
 }

class Solution {
 /**
 * @param A: An integer array.
 * @param B: An integer array.
 * @return: a double whose format is *.5 or *.0
 */
 	 public double findMedianSortedArrays(int[] A, int[] B) {
 		 // write your code here
 		 int len = A.length + B.length;
 		 if (len % 2 == 0) {
 			 return (findKth(A, 0, B, 0, len/2) + findKth(A, 0, B, 0, len/2+1)) / 2.0;
 		 } else {
	 			 return findKth(A, 0, B, 0, len/2 + 1);
 		 }
 	 }

 	 //find kth number of two sorted array
 	 public static int findKth(int[] A, int A_start, int[] B, int B_start, int k) {
 		 if (A_start >= A.length) {
 			 return B[B_start + k - 1];
 		 }
 		 if (B_start >= B.length) {
 			 return A[A_start + k - 1];
 		 }
 		 if (k == 1) {
 			 return Math.min(A[A_start], B[B_start]);
 		 }

 		 int A_key = (A_start + k/2 - 1 < A.length) // if one array is too short ? A[A_start + k/2 - 1] : Integer.MAX_VALUE; // trick
 		 int B_key = (B_start + k/2 - 1 < B.length) // if one array is too short ? B[B_start + k/2 - 1] : Integer.MAX_VALUE; // trick

 		 if (A_key < B_key) {
 			 return findKth(A, A_start + k/2, B, B_start, k - k/2);
 		 } else {
 			 return findKth(A, A_start, B, B_start + k/2, k - k/2);
 		 }
 	 }
 }

public class Solution {
 	 public int sqrt(int x) {
 		 long i = 0;
 		 long j = x / 2 + 1;
 		 while (i <= j) {
 			 long mid = (i + j) / 2;
 			 if (mid * mid == x)
 				 return (int)mid;
 			 if (mid * mid < x)
 				 i = mid + 1;
 			 else
 				 j = mid - 1;
 		 }
 		 return (int)j;
 	 }
 }

public class Solution {
 /**
 *@param L: Given n pieces of wood with length L[i]
 *@param k: An integer
 *return: The maximum length of the small pieces.
 */
 	 public int woodCut(int[] L, int k) {
 		 int max = 0;
 		 for (int i = 0; i < L.length; i++) {
 			 max = Math.max(max, L[i]);
 		 }

 		 // find the largest length that can cut more than k pieces of wood.
 		 int start = 1, end = max;
 		 while (start + 1 < end) {
 			 int mid = start + (end - start) / 2;
 			 if (count(L, mid) >= k) {
 				 start = mid;
 			 } else {
 				 end = mid;
 			 }
 		 }

 		 if (count(L, end) >= k) {
 			 return end;
 		 }
 		 if (count(L, start) >= k) {
 			 return start;
 		 }
 		 return 0;
 	 }

 	 private int count(int[] L, int length) {
 		 int sum = 0;
 		 for (int i = 0; i < L.length; i++) {
 			 sum += L[i] / length;
 		 }
 		 return sum;
 	 }
 }

public int singleNumber(int[] A) {
 	 HashSet<Integer> set = new HashSet<Integer>();
 	 for (int n : A) {
 		 if (!set.add(n))
 			 set.remove(n);
 	 }
 	 Iterator<Integer> it = set.iterator();
 	 return it.next();
 }

int singleNumber(int A[], int n) {
 	 vector<int> bit(32,0);

 	 for(int i =0; i< n; ++i)
 	 {
 		 int k=1;
 		 for(int j =0; j<32; ++j)
 		 {
 			 int rotated;
 			 if((rotated = A[i]>>j) == 0) break;
 			 bit[j] += rotated & k;
 		 }
 	 }

 	 int target=0;
 	 for(int i =0; i<32; ++i)
 	 {
 		 target += (bit[i]%3 <<i);
 	 }
 	 return target;
 }

Using O(1) time to check whether an integer n is a power of 2. 
 Example
 For n=4, return true; 
 For n=5, return false; 
 Challenge O(1) time

class Solution {
 /*
 * @param n: An integer
 * @return: True or false
 */
 	 public boolean checkPowerOf2(int n) {
 		 if (n < 1) {
 			 return false;
 		 } else {
 			 return (n & (n - 1)) == 0;
 		 }
 	 }
 };

class Solution {
 /**
 *@param a, b: Two integer
 *return: An integer
 */
 	 public static int bitSwapRequired(int a, int b) {
 		 int count = 0;
 		 int a_xor_b = a ^ b;
 		 while (a_xor_b != 0) {
 			 ++count;
 			 a_xor_b &= (a_xor_b - 1);
 		 }
 		 return count;
 	 }
 };

public class Solution {
 	 public int trailingZeroes(int n) {
 		 if (n <0) {
 			 return -1;
 		 }
 		 int count = 0;
 		 for (; n > 0; n /= 5) {
 			 count += (n / 5);
 		 }
 		 return count;
 	 }
 }

/**
 * http://www.jiuzhang.com/solutions/remove-duplicates-from-sorted-list/
 */

public class Solution {
 	 public ListNode deleteDuplicates(ListNode head) {
 		 if (head == null) {
 			 return null;
 		 }
 		 ListNode node = head;
 		 while (node.next != null) {
 			 if (node.val == node.next.val) {
 				 node.next = node.next.next;
 			 } else {
 				 node = node.next;
 			 }
 		 }
 		 return head;
 	 }
 }

public ListNode deleteDuplicates(ListNode head) {
 	 ListNode t = new ListNode(0);
 	 t.next = head;
 	 ListNode p = t;
 	 while(p.next!=null&&p.next.next!=null){
 		 if(p.next.val == p.next.next.val){
 			 int dup = p.next.val;
 			 while(p.next!=null&&p.next.val==dup){
 				 p.next = p.next.next;
 			 }
 		 }else{
 			 p=p.next;
 		 }
 	 }
 	 return t.next;
 }

public class Solution {
 	 public ListNode partition(ListNode head, int x) {
 		 if(head == null) return null;

 		 ListNode fakeHead1 = new ListNode(0);
 		 ListNode fakeHead2 = new ListNode(0);
 		 fakeHead1.next = head;

 		 ListNode p = head;
 		 ListNode prev = fakeHead1;
 		 ListNode p2 = fakeHead2;

 		 while(p != null){
 			 if(p.val < x){
 				 p = p.next;
 				 prev = prev.next;
 			 }else{
 				 p2.next = p;
 				 prev.next = p.next;
 				 p = prev.next;
 				 p2 = p2.next;
 			 }
 		 }
 		 // close the list
 		 p2.next = null;
 		 prev.next = fakeHead2.next;
 		 return fakeHead1.next;
 	 }
 }

public class Solution {
 	 public ListNode addTwoNumbers(ListNode l1, ListNode l2) {
 		 int carry =0;
 		 ListNode newHead = new ListNode(0);
 		 ListNode p1 = l1, p2 = l2, p3=newHead;
 		 while(p1 != null || p2 != null){
 			 if(p1 != null){
 				 carry += p1.val;
 			 p1 = p1.next;
 		 }
 		 if(p2 != null){
 			 carry += p2.val;
 			 p2 = p2.next;
 		 }
 		 p3.next = new ListNode(carry%10);
 		 p3 = p3.next;
 		 carry /= 10;
 	 }
 	 if(carry==1)
 		 p3.next=new ListNode(1);
 		 return newHead.next;
 	 }
 }

    // Java program to delete a given node in linked list under given constraints
\n
class LinkedList {\n
\n
    static Node head1, head2;
\n
    static class Node {
\n
        int data;\n
        Node next;\n
\n
        Node(int d) {\n
            data = d;\n
            next = null;\n
        }\n
    }\n
\n
    /* Adds contents of two linked lists and return the head node of resultant list */\n
    Node addTwoLists(Node first, Node second) {\n
        Node res = null; // res is head node of the resultant list\n
        Node prev = null;\n
        Node temp = null;\n
        int carry = 0, sum;\n
\n
        while (first != null || second != null) //while both lists exist{\n
            // Calculate value of next digit in resultant list.\n
            // The next digit is sum of following things\n
            // (i)  Carry\n
            // (ii) Next digit of first list (if there is a next digit)\n
            // (ii) Next digit of second list (if there is a next digit)\n
            sum = carry + (first != null ? first.data : 0)\n
                    + (second != null ? second.data : 0);\n
\n
            // update carry for next calulation\n
            carry = (sum >= 10) ? 1 : 0;\n
\n
            // update sum if it is greater than 10\n
            sum = sum % 10;\n
\n
            // Create a new node with sum as data\n
            temp = new Node(sum);\n
\n
            // if this is the first node then set it as head of\n
            // the resultant list\n
            if (res == null) {\n
                res = temp;\n
            } else // If this is not the first node then connect it to the rest.{\n
                prev.next = temp;\n
            }\n
\n
            // Set prev for next insertion\n
            prev = temp;\n
\n
            // Move first and second pointers to next nodes\n
            if (first != null) {\n
                first = first.next;\n
            }\n
            if (second != null) {\n
                second = second.next;\n
            }\n
        }\n
\n
        if (carry > 0) {\n
            temp.next = new Node(carry);\n
        }\n
\n
        // return head of the resultant list\n
        return res;\n
    }\n
    /* Utility function to print a linked list */\n
\n
    void printList(Node head) {\n
        while (head != null) {\n
            System.out.print(head.data + " ");\n
            head = head.next;\n
        }\n
        System.out.println("");\n
    }\n
\n
    public static void main(String[] args) {\n
        LinkedList list = new LinkedList();\n
\n
        // creating first list\n
        list.head1 = new Node(7);\n
        list.head1.next = new Node(5);\n
        list.head1.next.next = new Node(9);\n
        list.head1.next.next.next = new Node(4);\n
        list.head1.next.next.next.next = new Node(6);\n
        System.out.print("First List is ");\n
        list.printList(head1);\n
\n
        // creating seconnd list\n
        list.head2 = new Node(8);\n
        list.head2.next = new Node(4);\n
        System.out.print("Second List is ");\n
        list.printList(head2);\n
\n
        // add the two lists and see the result\n
        Node rs = list.addTwoLists(head1, head2);\n
        System.out.print("Resultant List is ");\n
        list.printList(rs);\n
    }\n
}

public ListNode removeNthFromEnd(ListNode head, int n) {
 	 if(head == null)
 		 return null;

 		 //get length of list
 		 ListNode p = head;
 		 int len = 0;
 		 while(p != null){
 			 len++;
 			 p = p.next;
 		 }

 		 //if remove first node
 		 int fromStart = len-n+1;
 		 if(fromStart==1)
 			 return head.next;

 		 //remove non-first node
 		 p = head;
 		 int i=0;
 		 while(p!=null){
 			 i++;
 			 if(i==fromStart-1){
 			 p.next = p.next.next;
 		 }
 		 p=p.next;
 	 }

 	 return head;
 }

public class Solution {
 	 public Boolean hasCycle(ListNode head) {
 		 if (head == null || head.next == null) {
 			 return false;
 		 }

 		 ListNode fast, slow;
 		 fast = head.next;
 		 slow = head;
 		 while (fast != slow) {
 			 if(fast==null || fast.next==null)
 				 return false;
 			 fast = fast.next.next;
 			 slow = slow.next;
 		 }
 		 return true;
 	 }
 }

public class Solution {
 	 public ListNode detectCycle(ListNode head) {
 		 if (head == null || head.next==null) {
 			 return null;
 		 }

 		 ListNode fast, slow;
 		 fast = head.next;
 		 slow = head;
 		 while (fast != slow) {
 			 if(fast==null || fast.next==null)
 				 return null;
 			 fast = fast.next.next;
 			 slow = slow.next;
 		 }

 		 while (head != slow.next) {
 			 head = head.next;
 			 slow = slow.next;
 		 }
 		 return head;
 	 }
 }

/**
 * Definition for ListNode.
 * public class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int val) {
 * this.val = val;
 * this.next = null;
 * }
 * }
 */
 public class Solution {
 	 /**
 	 * @param head: The head of linked list.
 	 * @return: The new head of reversed linked list.
 	 */
 	 public ListNode reverse(ListNode head) {
 		 ListNode prev = null;
 		 while (head != null) {
 			 ListNode temp = head.next;
 			 head.next = prev;
 			 prev = head;
 			 head = temp;
 		 }
 		 return prev;
 	 }
 }

/**
 * Definition for ListNode
 * public class ListNode {
 * int val;
 * ListNode next;
 * }
 */
 public class Solution {
 	 public ListNode reverseBetween(ListNode head, int m, int n) {
 		 if (m >= n || head == null) {
 			 return head;
 		 }

 		 ListNode dummy = new ListNode(0);
 		 dummy.next = head;
 		 head = dummy;

 		 for (int i = 1; i < m; i++) {
 			 if (head == null) {
 				 return null;
 			 }
 			 head = head.next;
 		 }

 		 ListNode premNode = head;
 		 ListNode mNode = head.next;
 		 ListNode nNode = mNode, postnNode = mNode.next;
 		 for (int i = m; i < n; i++) {
 			 if (postnNode == null) {
 				 return null;
 			 }
 			 ListNode temp = postnNode.next;
 			 postnNode.next = nNode;
 			 nNode = postnNode;
 			 postnNode = temp;
 		 }
 		 mNode.next = postnNode;
 		 premNode.next = nNode;

 		 return dummy.next;
 	 }
 }

public class Solution {
 	 public ListNode mergeTwoLists(ListNode l1, ListNode l2) {
 		 ListNode dummy = new ListNode(0);
 		 ListNode lastNode = dummy;

 		 while (l1 != null && l2 != null) {
 			 if (l1.val < l2.val) {
 				 lastNode.next = l1;
 				 l1 = l1.next;
 			 } else {
 				 lastNode.next = l2;
 				 l2 = l2.next;
 			 }
 			 lastNode = lastNode.next;
 		 }

 		 if (l1 != null) {
 			 lastNode.next = l1;
 		 } else {
 			 lastNode.next = l2;
 		 }

 		 return dummy.next;
 	 }
 }

/**
 * Definition for ListNode.
 *	 public class ListNode {
 *		 int val;
 * 		 ListNode next;
 * 		 ListNode(int val) {
 *			 this.val = val;
 * 			 this.next = null;
 * 		 }
 *	 }
 */
 public class Solution {
 	 /**
 	 * @param lists: a list of ListNode
 	 * @return: The head of one sorted list.
 	 */
 	 public ListNode mergeKLists(List<ListNode> lists) {
 		 if (lists.size() == 0) {
 			 return null;
 		 }
 		 return mergeHelper(lists, 0, lists.size() - 1);
 	 }

 	 private ListNode mergeHelper(List<ListNode> lists, int start, int end) {
 		 if (start == end) {
 			 return lists.get(start);
 		 }

 		 int mid = start + (end - start) / 2;
 		 ListNode left = mergeHelper(lists, start, mid);
 		 ListNode right = mergeHelper(lists, mid + 1, end);
 		 return mergeTwoLists(left, right);
 	 }

 	 private ListNode mergeTwoLists(ListNode list1, ListNode list2) {
 		 ListNode dummy = new ListNode(0);
 		 ListNode tail = dummy;
 		 while (list1 != null && list2 != null) {
 			 if (list1.val < list2.val) {
 				 tail.next = list1;
 				 tail = list1;
 				 list1 = list1.next;
 			 } else {
 				 tail.next = list2;
 				 tail = list2;
 				 list2 = list2.next;
 			 }
 		 }
 		 if (list1 != null) {
 			 tail.next = list1;
 		 } else {
 			 tail.next = list2;
 		 }

 		 return dummy.next;
 	 }
 }

// version 1: Merge Sort
 public class Solution {
 	 private ListNode findMiddle(ListNode head) {
 		 ListNode slow = head, fast = head.next;
 		 while (fast != null && fast.next != null) {
 			 fast = fast.next.next;
 			 slow = slow.next;
 		 }
 		 return slow;
 	 }

 	 private ListNode merge(ListNode head1, ListNode head2) {
 		 ListNode dummy = new ListNode(0);
 		 ListNode tail = dummy;
 		 while (head1 != null && head2 != null) {
 			 if (head1.val < head2.val) {
 				 tail.next = head1;
 				 head1 = head1.next;
 			 } else {
 				 tail.next = head2;
 				 head2 = head2.next;
 			 }
 				 tail = tail.next;
 			 }
 		 if (head1 != null) {
 			 tail.next = head1;
 		 } else {
 			 tail.next = head2;
 		 }

		 return dummy.next;
 	 }

 	 public ListNode sortList(ListNode head) {
 		 if (head == null || head.next == null) {
 			 return head;
 		 }

 		 ListNode mid = findMiddle(head);
 		 ListNode right = sortList(mid.next);
 		 mid.next = null;
 		 ListNode left = sortList(head);

 		 return merge(left, right);
 	 }
 }

public class Solution {
 	 private ListNode reverse(ListNode head) {
 		 ListNode newHead = null;
 		 while (head != null) {
 			 ListNode temp = head.next;
 			 head.next = newHead;
 			 newHead = head;
 			 head = temp;
 		 }
 		 return newHead;
 	 }

 	 private void merge(ListNode head1, ListNode head2) {
 		 int index = 0;
 		 ListNode dummy = new ListNode(0);
 		 while (head1 != null && head2 != null) {
 			 if (index % 2 == 0) {
 				 dummy.next = head1;
 				 head1 = head1.next;
 			 } else {
 				 dummy.next = head2;
 				 head2 = head2.next;
 			 }
 			 dummy = dummy.next;
 			 index ++;
 		 }
 		 if (head1 != null) {
 			 dummy.next = head1;
 		 } else {
 			 dummy.next = head2;
 		 }
 	 }

 	 private ListNode findMiddle(ListNode head) {
 		 ListNode slow = head, fast = head.next;
 		 while (fast != null && fast.next != null) {
 			 fast = fast.next.next;
 			 slow = slow.next;
 		 }
 		 return slow;
 	 }

 	 public void reorderList(ListNode head) {
 		 if (head == null || head.next == null) {
 			 return;
 		 }

 		 ListNode mid = findMiddle(head);
 		 ListNode tail = reverse(mid.next);
 		 mid.next = null;

 		 merge(head, tail);
 	 }
 }

public class Solution {
 	 /**
 	 * @param nums: The rotated sorted array
 	 * @return: The recovered sorted array
 	 */
 	 public void recoverRotatedSortedArray(ArrayList nums) {
 		 if (nums == null || nums.size() <= 1) {
 			 return;
 		 }

 		 int pos = 1;
 		 while (pos < nums.size()) { // find the break point
 			 if (nums.get(pos - 1) > nums.get(pos)) {
 				 break;
 			 }
 			 pos++;
 		 }
 		 myRotate(nums, 0, pos - 1);
 		 myRotate(nums, pos, nums.size() - 1);
 		 myRotate(nums, 0, nums.size() - 1);
 	 }

 	 private void myRotate(ArrayList nums, int left, int right) { // in-place rotate
 		 while (left < right) {
 			 int temp = nums.get(left);
 			 nums.set(left, nums.get(right));
 			 nums.set(right, temp);
 			 left++;
 			 right--;
 		 }
 	 }
 }

public class Solution {
 	 public String reverseWords(String s) {
 		 if (s == null || s.length() == 0) {
 			 return "";
 		 }

 		 String[] array = s.split(" ");
 		 StringBuilder sb = new StringBuilder();

 		 for (int i = array.length - 1; i >= 0; --i) {
 			 if (!array[i].equals("")) {
 				 sb.append(array[i]).append(" ");
 			 }
 		 }

 		 //remove the last " "
 		 return sb.length() == 0 ? "" : sb.substring(0, sb.length() - 1);
 	 }
 }

public void rotate(int[] nums, int k) {
 	 if(k > nums.length)
 	 k=k%nums.length;

 	 int[] result = new int[nums.length];

 	 for(int i=0; i < k; i++){
 		 result[i] = nums[nums.length-k+i];
 	 }

 	 int j=0;
 	 for(int i=k; i<nums.length; i++){
 		 result[i] = nums[j];
 		 j++;
 	 }
 	 System.arraycopy( result, 0, nums, 0, nums.length );
 }

public class Solution {
 	 public List<Integer> preorderTraversal(TreeNode root) {
 		 Stack<TreeNode> stack = new Stack<TreeNode>();
 		 List<Integer> preorder = new ArrayList<Integer>();

 		 if (root == null) {
 		 return preorder;
 	 }

 	 stack.push(root);
 	 while (!stack.empty()) {
 		 TreeNode node = stack.pop();
 		 preorder.add(node.val);
 		 if (node.right != null) {
 			 stack.push(node.right);
 		 }
 		 if (node.left != null) {
 			 stack.push(node.left);
 		 }
 		 }

 	 return preorder;
 	 }
 }

 //Version 1: Traverse
 public class Solution {
 	 public ArrayList<Integer> preorderTraversal(TreeNode root) {
 		 ArrayList<Integer> result = new ArrayList<Integer>();
 		 traverse(root, result);
 		 return result;
 	 }

 	 private void traverse(TreeNode root, ArrayList<Integer> result) {
 		 if (root == null) {
 			 return;
 		 }
 		 result.add(root.val);
 		 traverse(root.left, result);
 		 traverse(root.right, result);
 	 }
 }

public class Solution {
 /**
 * @param root: The root of binary tree.
 * @return: Inorder in ArrayList which contains node values.
 */
 public ArrayList<Integer>inorderTraversal(TreeNode root) {
 Stack<TreeNode> stack = new Stack<TreeNode>();
 ArrayList<Integer> result = new ArrayList<Integer>();
 TreeNode curt = root;
 while (curt != null || !stack.empty()) {
 while (curt != null) {
 stack.add(curt);
 curt = curt.left;
 }
 curt = stack.peek();
 stack.pop();
 result.add(curt.val);
 curt = curt.right;
 }
 return result;
 }
 }

    //Recursive\n
public ArrayList<Integer> postorderTraversal(TreeNode root) {\n
    ArrayList<Integer> result = new ArrayList<Integer>();\n
    if (root == null) {\n
        return result;\n
    }\n
    result.addAll(postorderTraversal(root.left));\n
    result.addAll(postorderTraversal(root.right));\n
    result.add(root.val);\n
    return result;\n
}\n
//Iterative\n
public ArrayList<Integer> postorderTraversal(TreeNode root) {\n
    ArrayList<Integer> result = new ArrayList<Integer>();\n
    Stack<TreeNode> stack = new Stack<TreeNode>();\n
    TreeNode prev = null; // previously traversed node\n
    TreeNode curr = root;\n
    if (root == null) {\n
        return result;\n
    }\n
    stack.push(root);\n
    while (!stack.empty()) {\n
        curr = stack.peek();\n
        if (prev == null || prev.left == curr || prev.right == curr) { // traverse down the tree\n
            if (curr.left != null) {\n
                stack.push(curr.left);\n
            } else if (curr.right != null) {\n
                stack.push(curr.right);\n
            }\n
        } else if (curr.left == prev) { // traverse up the tree from the left\n
            if (curr.right != null) {\n
                stack.push(curr.right);\n
            }\n
        } else { // traverse up the tree from the right\n
            result.add(curr.val);\n
            stack.pop();\n
        }\n
        prev = curr;\n
    }\n
\n
    return result;\n
}

// version 1: BFS
 public class Solution {
 public ArrayList<ArrayList<Integer>> levelOrder(TreeNode root) {
 ArrayList result = new ArrayList();
 if (root == null) {
 return result;
 }

 Queue<TreeNode> queue = new LinkedList<TreeNode>();
 queue.offer(root);

 while (!queue.isEmpty()) {
 ArrayList<Integer> level = new ArrayList<Integer>();
 int size = queue.size();
 for (int i = 0; i < size; i++) {
 TreeNode head = queue.poll();
 level.add(head.val);
 if (head.left != null) {
 queue.offer(head.left);
 }
 if (head.right != null) {
 queue.offer(head.right);
 }
 }
 result.add(level);
 }
 return result;
 }
 }

public class Solution {
 public int maxDepth(TreeNode root) {
 if (root == null) {
 return 0;
 }

 int left = maxDepth(root.left);
 int right = maxDepth(root.right);
 return Math.max(left, right) + 1;
 }
 }

 // version 2: Traverse

 /**
 * Definition of TreeNode:
 * public class TreeNode {
 * public int val;
 * public TreeNode left, right;
 * public TreeNode(int val) {
 * this.val = val;
 * this.left = this.right = null;
 * }
 * }
 */
 public class Solution {
 /**
 * @param root: The root of binary tree.
 * @return: An integer.
 */
 private int depth;

 public int maxDepth(TreeNode root) {
 depth = 0;
 helper(root, 1);
 return depth;
 }
 private void helper(TreeNode node, int curtDepth) {
 if (node == null) {
 return;
 }
 if (curtDepth > depth) {
 depth = curtDepth;
 }
 helper(node.left, curtDepth + 1);
 helper(node.right, curtDepth + 1);
 }
 }

// Version 1: with ResultType
 /**
 * Definition of TreeNode:
 * public class TreeNode {
 * public int val;
 * public TreeNode left, right;
 * public TreeNode(int val) {
 * this.val = val;
 * this.left = this.right = null;
 * }
 * }
 */
 class ResultType {
 public boolean isBalanced;
 public int maxDepth;
 public ResultType(boolean isBalanced, int maxDepth) {
 this.isBalanced = isBalanced;
 this.maxDepth = maxDepth;
 }
 }

 public class Solution {
 /**
 * @param root: The root of binary tree.
 * @return: True if this Binary tree is Balanced, or false.
 */
 public boolean isBalanced(TreeNode root) {
 return helper(root).isBalanced;
 }

 private ResultType helper(TreeNode root) {
 if (root == null) {
 return new ResultType(true, 0);
 }

 ResultType left = helper(root.left);
 ResultType right = helper(root.right);

 // subtree not balance
 if (!left.isBalanced || !right.isBalanced) {
 return new ResultType(false, -1);
 }

 // root not balance
 if (Math.abs(left.maxDepth - right.maxDepth) > 1) {
 return new ResultType(false, -1);
 }

 return new ResultType(true, Math.max(left.maxDepth, right.maxDepth) + 1);
 }
 }

 // Version 2: without ResultType
 public class Solution {
 public boolean isBalanced(TreeNode root) {
 return maxDepth(root) != -1;
 }

 private int maxDepth(TreeNode root) {
 if (root == null) {
 return 0;
 }

 int left = maxDepth(root.left);
 int right = maxDepth(root.right);
 if (left == -1 || right == -1 || Math.abs(left-right) > 1) {
 return -1;
 }
 return Math.max(left, right) + 1;
 }
 }

public class Solution {
 private class ResultType {
 // singlePath:
 // maxPath:
 int singlePath, maxPath;
 ResultType(int singlePath, int maxPath) {
 this.singlePath = singlePath;
 this.maxPath = maxPath;
 }
 }

 private ResultType helper(TreeNode root) {
 if (root == null) {
 return new ResultType(0, Integer.MIN_VALUE);
 }
 // Divide
 ResultType left = helper(root.left);
 ResultType right = helper(root.right);

 // Conquer
 int singlePath = Math.max(left.singlePath, right.singlePath) + root.val;
 singlePath = Math.max(singlePath, 0);

 int maxPath = Math.max(left.maxPath, right.maxPath);
 maxPath = Math.max(maxPath, left.singlePath + right.singlePath + root.val);

 return new ResultType(singlePath, maxPath);
 }

 public int maxPathSum(TreeNode root) {
 ResultType result = helper(root);
 return result.maxPath;
 }
 }

Version 1: Traditional Method
\n
public class Solution {\n
    private ArrayList<TreeNode> getPath2Root(TreeNode node) {\n
        ArrayList<TreeNode> list = new ArrayList<TreeNode>();\n
        while (node != null) {\n
            list.add(node);\n
            node = node.parent;\n
        }\n
        return list;\n
    }\n
    public TreeNode lowestCommonAncestor(TreeNode node1, TreeNode node2) {\n
        ArrayList<TreeNode> list1 = getPath2Root(node1);\n
        ArrayList<TreeNode> list2 = getPath2Root(node2);\n
\n
        int i, j;\n
        for (i = list1.size() - 1, j = list2.size() - 1; i >= 0 && j >= 0; i--, j--) {\n
            if (list1.get(i) != list2.get(j)) {\n
                return list1.get(i).parent;\n
            }\n
        }\n
        return list1.get(i+1);\n
    }\n
}\n
Version 2: Divide & Conquer\n
public class Solution {\n
    public TreeNode lowestCommonAncestor(TreeNode root, TreeNode node1, TreeNode node2) {\n
        if (root == null || root == node1 || root == node2) {\n
            return root;\n
        }\n
        // Divide\n
        TreeNode left = lowestCommonAncestor(root.left, node1, node2);\n
        TreeNode right = lowestCommonAncestor(root.right, node1, node2);\n
        // Conquer\n
        if (left != null && right != null) {\n
            return root;\n
        }\n
        if (left != null) {\n
            return left;\n
        }\n
        if (right != null) {\n
            return right;\n
        }\n
        return null;\n
    }\n
}

version 1:

 /**
 * Definition of TreeNode:
 * public class TreeNode {
 * public int val;
 * public TreeNode left, right;
 * public TreeNode(int val) {
 * this.val = val;
 * this.left = this.right = null;
 * }
 * }
 */
 public class Solution {
 /**
 * @param root: The root of the binary search tree.
 * @param node: insert this node into the binary search tree
 * @return: The root of the new binary search tree.
 */
 public TreeNode insertNode(TreeNode root, TreeNode node) {
 if (root == null) {
 root = node;
 return root;
 }
 TreeNode tmp = root;
 TreeNode last = null;
 while (tmp != null) {
 last = tmp;
 if (tmp.val > node.val) {
 tmp = tmp.left;
 } else {
 tmp = tmp.right;
 }
 }
 if (last != null) {
 if (last.val > node.val) {
 last.left = node;
 } else {
 last.right = node;
 }
 }
 return root;
 }
 }
