 /**
 * Definition of TreeNode:
 * public class TreeNode {
 * public int val;
 * public TreeNode left, right;
 * public TreeNode(int val) {
 * this.val = val;
 * this.left = this.right = null;
 * }
 * }
 */
 public class Solution {
 /**
 * @param root: The root of the binary search tree.
 * @param node: insert this node into the binary search tree
 * @return: The root of the new binary search tree.
 */
 public TreeNode insertNode(TreeNode root, TreeNode node) {
 if (root == null) {
 return node;
 }
 if (root.val > node.val) {
 root.left = insertNode(root.left, node);
 } else {
 root.right = insertNode(root.right, node);
 }
 return root;
 }
 }

// version 1 Traverse
 public class Solution {
 private int lastVal = Integer.MIN_VALUE;
 private boolean firstNode = true;
 public boolean isValidBST(TreeNode root) {
 if (root == null) {
 return true;
 }
 if (!isValidBST(root.left)) {
 return false;
 }
 if (!firstNode && lastVal >= root.val) {
 return false;
 }
 firstNode = false;
 lastVal = root.val;
 if (!isValidBST(root.right)) {
 return false;
 }
 return true;
 }
 }

 // version 2 Divide and Conquer
 class ResultType {
 boolean is_bst;
 int maxValue, minValue;

 ResultType(boolean is_bst, int maxValue, int minValue) {
 this.is_bst = is_bst;
 this.maxValue = maxValue;
 this.minValue = minValue;
 }
 }

 public class Solution {
 /**
 * @param root: The root of binary tree.
 * @return: True if the binary tree is BST, or false
 */
 public boolean isValidBST(TreeNode root) {
 ResultType r = validateHelper(root);
 return r.is_bst;
 }

 private ResultType validateHelper(TreeNode root) {
 if (root == null) {
 return new ResultType(true, Integer.MIN_VALUE, Integer.MAX_VALUE);
 }

 ResultType left = validateHelper(root.left);
 ResultType right = validateHelper(root.right);

 if (!left.is_bst || !right.is_bst) {
 // if is_bst is false then minValue and maxValue are useless
 return new ResultType(false, 0, 0);
 }

 if (root.left != null && left.maxValue >= root.val ||
 root.right != null && right.minValue <= root.val) {
 return new ResultType(false, 0, 0);
 }

 return new ResultType(true,
 Math.max(root.val, right.maxValue),
 Math.min(root.val, left.minValue));
 }
 }

/**
 * Definition of TreeNode:
 * public class TreeNode {
 * public int val;
 * public TreeNode left, right;
 * public TreeNode(int val) {
 * this.val = val;
 * this.left = this.right = null;
 * }
 * }
 */
 public class Solution {
 	 private ArrayList<Integer> results;
 /**
 * @param root: The root of the binary search tree.
 * @param k1 and k2: range k1 to k2.
 * @return: Return all keys that k1<=key<=k2 in increasing order.
 */
 	 public ArrayList<Integer> searchRange(TreeNode root, int k1, int k2) {
 		 results = new ArrayList<Integer>();
 		 helper(root, k1, k2);
 		 return results;
 	 }

 	 private void helper(TreeNode root, int k1, int k2) {
 		 if (root == null) {
 			 return;
 		 }
 		 if (root.val > k1) {
 			 helper(root.left, k1, k2);
 		 }
 		 if (root.val >= k1 && root.val <= k2) {
 			 results.add(root.val);
 		 }
 		 if (root.val < k2) {
 			 helper(root.right, k1, k2);
 		 }
 	 }
 }

public class Solution {
 	 private TreeNode buildTree(int[] num, int start, int end) {
 		 if (start > end) {
 			 return null;
 		 }

 		 TreeNode node = new TreeNode(num[(start + end) / 2]);
 		 node.left = buildTree(num, start, (start + end) / 2 - 1);
 		 node.right = buildTree(num, (start + end) / 2 + 1, end);
 		 return node;
 	 }

 	 public TreeNode sortedArrayToBST(int[] num) {
 		 if (num == null) {
 			 return null;
 		 }
 		 return buildTree(num, 0, num.length - 1);
 	 }
 }

public class Solution {
 	 private ListNode current;

 	 private int getListLength(ListNode head) {
 		 int size = 0;

 		 while (head != null) {
 			 size++;
 			 head = head.next;
 		 }

 		 return size;
 	 }

 	 public TreeNode sortedListToBST(ListNode head) {
 		 int size;

 		 current = head;
 		 size = getListLength(head);

 		 return sortedListToBSTHelper(size);
 	 }

 	 public TreeNode sortedListToBSTHelper(int size) {
 		 if (size <= 0) {
 			 return null;
 		 }

 		 TreeNode left = sortedListToBSTHelper(size / 2);
 		 TreeNode root = new TreeNode(current.val);
 		 current = current.next;
 		 TreeNode right = sortedListToBSTHelper(size - 1 - size / 2);

 		 root.left = left;
 		 root.right = right;

 		 return root;
 	 }
 }

/**
 * Definition of TreeNode:
 * public class TreeNode {
 * public int val;
 * public TreeNode left, right;
 * public TreeNode(int val) {
 * this.val = val;
 * this.left = this.right = null;
 * }
 * }
 * Example of iterate a tree:
 * Solution iterator = new Solution(root);
 * while (iterator.hasNext()) {
 * TreeNode node = iterator.next();
 * do something for node
 * }
 */
 public class Solution {
 	 private Stack stack = new Stack<>();
 	 private TreeNode curt;

 	 // @param root: The root of binary tree.
 	 public Solution(TreeNode root) {
 		 curt = root;
 	 }

 	 //@return: True if there has next node, or false
 	 public boolean hasNext() {
 		 return (curt != null || !stack.isEmpty()); //important to judge curt != null
 	 }

 	 //@return: return next node
 	 public TreeNode next() {
 		 while (curt != null) {
 			 stack.push(curt);
 			 curt = curt.left;
 		 }

 		 curt = stack.pop();
 		 TreeNode node = curt;
 		 curt = curt.right;

 		 return node;
 	 }
 }

public class Solution {
 	 public ArrayList<ArrayList<Integer>> subsets(int[] num) {
 		 ArrayList<ArrayList<Integer>> result = new ArrayList<ArrayList<Integer>>();
 		 if(num == null || num.length == 0) {
 			 return result;
 		 }
 		 ArrayList<Integer> list = new ArrayList<Integer>();
 		 Arrays.sort(num);
 		 subsetsHelper(result, list, num, 0);

 		 return result;
 	 }

 	 private void subsetsHelper(ArrayList<ArrayList<Integer>> result,
 		 ArrayList<Integer> list, int[] num, int pos) {

 		 result.add(new ArrayList<Integer>(list));

 		 for (int i = pos; i < num.length; i++) {

 			 list.add(num[i]);
 			 subsetsHelper(result, list, num, i + 1);
 			 list.remove(list.size() - 1);
 		 }
 	 }
 }

 // Non Recursion
 class Solution {
 	 /**
 	 * @param S: A set of numbers.
 	 * @return: A list of lists. All valid subsets.
 	 */
 	 public ArrayList<ArrayList<Integer>> subsets(int[] nums) {
 		 ArrayList<ArrayList<Integer>> result = new ArrayList<ArrayList<Integer>>();
 		 int n = nums.length;
 		 Arrays.sort(nums);

 		 // 1 << n is 2^n
 		 // each subset equals to an binary integer between 0 .. 2^n - 1
 		 // 0 -> 000 -> []
 		 // 1 -> 001 -> [1]
 		 // 2 -> 010 -> [2]
 		 // ..
 		 // 7 -> 111 -> [1,2,3]
 		 for (int i = 0; i < (1 << n); i++) {
 			 ArrayList<Integer> subset = new ArrayList<Integer>();
 			 for (int j = 0; j < n; j++) {
 				 // check whether the jth digit in i's binary representation is 1
 				 if ((i & (1 << j)) != 0) {
 					 subset.add(nums[j]);
 				 }
 			 }
 			 result.add(subset);
 		 }
 		 return result;
 	 }
 }

public class Solution {
 	 public ArrayList<ArrayList<Integer>> permute(int[] num) {
 		 ArrayList<ArrayList<Integer>> rst = new ArrayList<ArrayList<Integer>>();
 		 if (num == null || num.length == 0) {
 			 return rst;
 		 }

 		 ArrayList<Integer> list = new ArrayList<Integer>();
 		 helper(rst, list, num);
 		 return rst;
 	 }

 	 public void helper(ArrayList<ArrayList<Integer>> rst, ArrayList<Integer> list, int[] num){
 		 if(list.size() == num.length) {
 			 rst.add(new ArrayList<Integer>(list));
 			 return;
 		 }

 		 for(int i = 0; i<num.length; i++){
 			 if(list.contains(num[i])){
 				 continue;
 			 }
 			 list.add(num[i]);
 			 helper(rst, list, num);
 			 list.remove(list.size() - 1);
 		 }
 	 }
 }
 // Non-Recursion
 class Solution {
 	 /**
 	 * @param nums: A list of integers.
 	 * @return: A list of permutations.
 	 */
 	 public ArrayList<ArrayList<Integer>> permute(ArrayList<Integer> nums) {
 		 ArrayList<ArrayList<Integer>> permutations
 		 = new ArrayList<ArrayList<Integer>>();
 		 if (nums == null || nums.size() == 0) {
 			 return permutations;
 		 }

 		 int n = nums.size();
 		 ArrayList<Integer> stack = new ArrayList<Integer>();

 		 stack.add(-1);
 		 while (stack.size() != 0) {
 			 Integer last = stack.get(stack.size() - 1);
 			 stack.remove(stack.size() - 1);

 		 // increase the last number
 		 int next = -1;
 		 for (int i = last + 1; i < n; i++) {
 			 if (!stack.contains(i)) {
 				 next = i;
 				 break;
 			 }
 		 }
 		 if (next == -1) {
 			 continue;
 		 }

 		 // generate the next permutation
 		 stack.add(next);
 		 for (int i = 0; i < n; i++) {
 			 if (!stack.contains(i)) {
 				 stack.add(i);
 			 }
 		 }

 		 // copy to permutations set
 		 ArrayList<Integer> permutation = new ArrayList<Integer>();
 		 for (int i = 0; i < n; i++) {
 			 permutation.add(nums.get(stack.get(i)));
 		 }
 		 permutations.add(permutation);
 		 }
 		 return permutations;
 	 }
 }

// version 0: top-down
 public class Solution {
 	 /**
 	 * @param triangle: a list of lists of integers.
 	 * @return: An integer, minimum path sum.
 	 */
 	 public int minimumTotal(int[][] triangle) {
 		 if (triangle == null || triangle.length == 0) {
 			 return -1;
 		 }
 		 if (triangle[0] == null || triangle[0].length == 0) {
 			 return -1;
 		 }

 		 // state: f[x][y] = minimum path value from 0,0 to x,y
 		 int n = triangle.length;
 		 int[][] f = new int[n][n];

 		 // initialize
 		 f[0][0] = triangle[0][0];
 		 for (int i = 1; i < n; i++) {
 			 f[i][0] = f[i - 1][0] + triangle[i][0];
 			 f[i][i] = f[i - 1][i - 1] + triangle[i][i];
 		 }

 		 // top down
 		 for (int i = 1; i < n; i++) {
 			 for (int j = 1; j < i; j++) {
 				 f[i][j] = Math.min(f[i - 1][j], f[i - 1][j - 1]) + triangle[i][j];
 			 }
 		 }

 		 // answer
 		 int best = f[n - 1][0];
 		 for (int i = 1; i < n; i++) {
 			 best = Math.min(best, f[n - 1][i]);
 		 }
 		 return best;
 	 }
 }

 //Version 1: Bottom-Up
 public class Solution {
 	 /**
 	 * @param triangle: a list of lists of integers.
 	 * @return: An integer, minimum path sum.
 	 */
 	 public int minimumTotal(int[][] triangle) {
 		 if (triangle == null || triangle.length == 0) {
 			 return -1;
 		 }
 		 if (triangle[0] == null || triangle[0].length == 0) {
 			 return -1;
 		 }
 		 // state: f[x][y] = minimum path value from x,y to bottom
 		 int n = triangle.length;
 		 int[][] f = new int[n][n];

 		 // initialize
 		 for (int i = 0; i < n; i++) {
 			 f[n - 1][i] = triangle[n - 1][i];
 		 }
 		 // bottom up
 		 for (int i = n - 2; i >= 0; i--) {
 			 for (int j = 0; j <= i; j++) {
 				 f[i][j] = Math.min(f[i + 1][j], f[i + 1][j + 1]) + triangle[i][j];
 			 }
 		 }
 		 // answer
 		 return f[0][0];
 	 }
 }
 //Version 2 : Memorize Search
 public class Solution {
 	 private int n;
 	 private int[][] minSum;
 	 private int[][] triangle;

 	 private int search(int x, int y) {
 		 if (x >= n) {
 			 return 0;
 		 }

 		 if (minSum[x][y] != Integer.MAX_VALUE) {
 			 return minSum[x][y];
 		 }

 		 minSum[x][y] = Math.min(search(x + 1, y), search(x + 1, y + 1))
 		 + triangle[x][y];
 		 return minSum[x][y];
 	 }

 	 public int minimumTotal(int[][] triangle) {
 		 if (triangle == null || triangle.length == 0) {
 			 return -1;
 		 }
 		 if (triangle[0] == null || triangle[0].length == 0) {
 			 return -1;
 		 }

 		 this.n = triangle.length;
 		 this.triangle = triangle;
 		 this.minSum = new int[n][n];

 		 for (int i = 0; i < n; i++) {
 			 for (int j = 0; j < n; j++) {
 				 minSum[i][j] = Integer.MAX_VALUE;
 			 }
 		 }
 		 return search(0, 0);
 	 }
 }

public class Solution {
 	 /**
 	 * @param m: An integer m denotes the size of a backpack
 	 * @param A: Given n items with size A[i]
 	 * @return: The maximum size
 	 */
 	 public int backPack(int m, int[] A) {
 		 boolean f[][] = new boolean[A.length + 1][m + 1];
 		 for (int i = 0; i <= A.length; i++) {
 			 fo	r (int j = 0; j <= m; j++) {
 				 f[i][j] = false;
 			 }
 		 }
 		 f[0][0] = true;
 		 for (int i = 0; i < A.length; i++) {
 			 fo	r (int j = 0; j <= m; j++) {
 				 f[i + 1][j] = f[i][j];
 				 if (j >= A[i] && f[i][j - A[i]]) {
 					 f[i + 1][j] = true;
 				 }
 			 } // for j
 		 } // for i

 		 for (int i = m; i >= 0; i--) {
 			 if (f	[A.length][i]) {
 				 return i;
 			 }
 		 }
 		 return 0;
 	 }
 }

public class Solution {
 	 public int minPathSum(int[][] grid) {
 		 if (grid == null || grid.length == 0 || grid[0].length == 0) {
 			 return 0;
 		 }

 		 int M = 	grid.length;
 		 int N = grid[0].length;
 		 int[][] sum = new int[M][N];

 		 sum[0][0] = grid[0][0];

 		 for (int i = 1; i < M; i++) {
 			 sum[i][0] = sum[i - 1][0] + grid[i][0];
 		 }

 		 for (int i = 1; i < N; i++) {
 			 sum[0][i] = sum[0][i - 1] + grid[0][i];
 		 }

 		 for (int i = 1; i < M; i++) {
 			 for (int j = 1; j < N; j++) {
 				 sum[i][j] = Math.min(sum[i - 1][j], sum[i][j - 1]) + grid[i][j];
 			 }
 		 }

 		 return sum[M - 1][N - 1];
 	 }
 }

public class Solution {
 	 public int uniquePaths(int m, int n) {
 		 if (m == 0 || n == 0) {
 			 return 0;
 		 }
 		 int[][] sum = new int[m][n];
 		 for (int i = 0; i < m; i++) {
 			 sum[i][0] = 1;
 		 }
 		 for (int i = 0; i < n; i++) {
 			 sum[0][i] = 1;
 		 }
 		 for (int i = 1; i < m; i++) {
 			 for (int j = 1; j < n; j++) {
 				 sum[i][j] = sum[i - 1][j] + sum[i][j - 1];
 			 }
 		 }
 		 return sum[m - 1][n - 1];
 	 }
 }

public class Solution {
 	 public int climbStairs(int n) {
 		 if (n <= 1) {
 			 return 1;
 		 }
 		 int last = 1, lastlast = 1;
 		 int now = 0;
 		 for (int i = 2; i <= n; i++) {
 			 now = last + lastlast;
 			 lastlast = last;
 			 last = now;
 		 }
 		 return now;
 	 }
 }

// version 1: Dynamic Programming
 public class Solution {
 	 public boolean canJump(int[] A) {
 		 boolean[] can = new boolean[A.length];
 		 can[0] = true;

 		 for (int i = 1; i < A.length; i++) {
 			 for (int j = 0; j < i; j++) {
 				 if (can[j] && j + A[j] >= i) {
 					 can[i] = true;
 					 break;
 				 }
 			 }
 		 }

 		 return can[A.length - 1];
 	 }
 }

 // version 2: Greedy
 public class Solution {
 	 public boolean canJump(int[] A) {
 		 // think it as merging n intervals
 		 if (A == null || A.length == 0) {
 			 return false;
 		 }
 		 int farthest = A[0];
 		 for (int i = 1; i < A.length; i++) {
 			 if (i <= farthest && A[i] + i >= farthest) {
 				 farthest = A[i] + i;
 			 }
 		 }
 		 return farthest >= A.length - 1;
 	 }
 }
